INSERT INTO sms_templates (slug, name, description, category, status, body, variables, triggers)
VALUES
  (
    'launch-friends-family-pitch',
    'Launch: Friends & Family Pitch',
    'Initial text to friends & family inviting them to the $1 founding tester offer',
    'marketing',
    'active',
    E'Hey! We just finished building our VibrationFit platform and I''m running a tiny "friends & family" test of our 72-Hour Activation Intensive. It''ll be $499 when we launch, but I''m giving this first round for $1, and it also includes 8 weeks of membership starting when you join. All I''m asking for is honest feedback (and a testimonial if you love it). Want me to send you the link?',
    '[]'::jsonb,
    '["Manual send from CRM to friends & family list"]'::jsonb
  ),
  (
    'launch-friends-family-link',
    'Launch: Friends & Family Link',
    'Follow-up text with the $1 founding tester link after they say yes',
    'marketing',
    'active',
    E'Amazing, thank you! Here''s the $1 founding tester link for the 72-Hour Activation Intensive + 8 weeks of membership: https://vibrationfit.com/?promo=LAUNCH2026\n\nWe''re only taking about 15-20 people in this first round, so grab it when you can.',
    '[]'::jsonb,
    '["Manual send after prospect replies yes"]'::jsonb
  ),
  (
    'launch-pre-renewal-reminder',
    'Launch: Pre-Renewal Reminder',
    'SMS reminder 3-5 days before Vision Pro membership renewal',
    'reminders',
    'active',
    'Hey {{firstName}}, quick reminder: your VibrationFit Vision Pro membership is set to renew on {{renewalDate}} at {{amount}} for 12 months. If you''re loving it, you''re all set. If you''d rather cancel before it renews, you can do that here: {{manageLink}}. Just want to make sure there are no surprises.',
    '["firstName", "renewalDate", "amount", "manageLink"]'::jsonb,
    '["Scheduled: 3-5 days before renewal date"]'::jsonb
  )
