INSERT INTO email_templates (slug, name, description, category, status, subject, html_body, text_body, variables, triggers)
VALUES

-- -------------------------------------------------------
-- 1. Friends & Family Invite Email
-- -------------------------------------------------------
(
  'launch-friends-family',
  'Launch: Friends & Family Invite',
  'Email pitch to friends & family for the $1 founding tester offer',
  'marketing',
  'active',
  'Want to test our 72-Hour Activation Intensive for $1?',
  E'<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <meta name="viewport" content="width=device-width, initial-scale=1.0">\n</head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;">\n<tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;">\n  <div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;">\n    <p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Founding Tester Invitation</p>\n  </div>\n</td></tr>\n\n<tr><td style="padding:0;">\n<div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Hey {{firstName}},</h1>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">Our VibrationFit platform is finally ready, and we''re opening our first 72-Hour Activation Intensive to a tiny "friends &amp; family" test group.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">We''d love for you to be part of it.</p>\n\n  <div style="margin:24px 0;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Here''s the deal</p>\n    <table width="100%" cellpadding="0" cellspacing="0">\n      <tr><td style="padding:8px 0;color:#E5E5E5;font-size:15px;line-height:1.5;"><span style="color:#39FF14;margin-right:8px;">&#10003;</span> Full 72-Hour Activation Intensive</td></tr>\n      <tr><td style="padding:8px 0;color:#E5E5E5;font-size:15px;line-height:1.5;"><span style="color:#39FF14;margin-right:8px;">&#10003;</span> 8 weeks of VibrationFit membership</td></tr>\n      <tr><td style="padding:8px 0;color:#E5E5E5;font-size:15px;line-height:1.5;"><span style="color:#39FF14;margin-right:8px;">&#10003;</span> Regular price: <span style="text-decoration:line-through;color:#999;">$499</span></td></tr>\n      <tr><td style="padding:8px 0;color:#39FF14;font-size:18px;font-weight:700;line-height:1.5;">Founding tester price: $1</td></tr>\n    </table>\n  </div>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">Why $1?</p>\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">Because this round is about testing and improving, not making money. I want honest feedback, bug reports, and (if it genuinely helps you) a testimonial we can use when we launch publicly.</p>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">What I ask from you:</p>\n  <p style="margin:0 0 4px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; Go through the 72-hour experience</p>\n  <p style="margin:0 0 4px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; Tell us what you loved, what was confusing, and what you''d change</p>\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; If it helps you, be open to a short testimonial</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center">\n    <a href="https://vibrationfit.com/?promo=LAUNCH2026" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Grab Your $1 Spot</a>\n  </td></tr></table>\n\n  <p style="margin:0;font-size:14px;line-height:1.6;color:#999;text-align:center;">Only 15-20 spots. Your 8 weeks start the day you sign up.</p>\n\n</div>\n</td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;">\n  <p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p>\n</td></tr>\n\n</table>\n</td></tr>\n</table>\n</body>\n</html>',
  E'Hey {{firstName}},\n\nOur VibrationFit platform is finally ready, and we''re opening our first 72-Hour Activation Intensive to a tiny "friends & family" test group.\n\nWe''d love for you to be part of it.\n\nHERE''S THE DEAL\n\n- Full 72-Hour Activation Intensive\n- 8 weeks of VibrationFit membership\n- Regular price: $499\n- Founding tester price: $1\n\nWhy $1? Because this round is about testing and improving, not making money. I want honest feedback, bug reports, and (if it genuinely helps you) a testimonial we can use when we launch publicly.\n\nWhat the 72-Hour Activation Intensive is about:\n- Getting crystal clear on your vision for your life\n- Getting into vibrational harmony with that vision\n- Starting to turn that vision into real, tangible experiences\n\nWhat I ask from you:\n- Go through the 72-hour experience\n- Tell us what you loved, what was confusing, and what you''d change\n- If it helps you, be open to a short testimonial\n\nGrab your $1 spot: https://vibrationfit.com/?promo=LAUNCH2026\n\nOnly 15-20 spots. Your 8 weeks start the day you sign up.\n\nThank you for helping us get this into the world,\nJordan',
  '["firstName"]'::jsonb,
  '["Manual send to friends & family list"]'::jsonb
),

-- -------------------------------------------------------
-- 2. Pre-Renewal Reminder Email
-- -------------------------------------------------------
(
  'launch-pre-renewal',
  'Launch: Pre-Renewal Reminder',
  'Heads up email before $1 tester membership renews to paid plan',
  'billing',
  'active',
  'Quick heads up about your VibrationFit membership',
  E'<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <meta name="viewport" content="width=device-width, initial-scale=1.0">\n</head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;">\n<tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;">\n  <div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;">\n    <p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Membership Update</p>\n  </div>\n</td></tr>\n\n<tr><td style="padding:0;">\n<div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Hey {{firstName}},</h1>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">Quick heads up so there are no surprises.</p>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">When you joined the 72-Hour Activation Intensive, you also unlocked 8 weeks of Vision Pro membership as part of that $1 founding tester offer.</p>\n\n  <div style="margin:24px 0;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 8px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Renewal Details</p>\n    <p style="margin:0 0 16px;font-size:16px;color:#fff;font-weight:600;">On {{renewalDate}}, your membership renews to:</p>\n    <p style="margin:0 0 4px;font-size:24px;color:#39FF14;font-weight:700;">{{planName}} &mdash; {{amount}}</p>\n    <p style="margin:0;font-size:14px;color:#999;">Billed once, locked in for the year.</p>\n  </div>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">That renewal includes:</p>\n  <p style="margin:0 0 4px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; Full access to the Vision Builder and all creation tools</p>\n  <p style="margin:0 0 4px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; Unlimited vision boards, audios, journaling, and progress tracking</p>\n  <p style="margin:0 0 4px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; Your satisfaction guarantee window from the membership terms</p>\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">&#8226; Priority access to new features and future activations</p>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">If you''re loving the platform, you don''t need to do anything. Your membership will renew automatically on {{renewalDate}}.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">If you don''t want to continue, no hard feelings at all. You can cancel any time before {{renewalDate}}:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center">\n    <a href="{{manageLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Manage My Membership</a>\n  </td></tr></table>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">I''d much rather be overly clear now than have you feel surprised later. If you have any questions, just hit reply.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">Thank you again for being part of the first group inside VibrationFit. You helped us bring this to life.</p>\n  <p style="margin:16px 0 0;font-size:15px;color:#E5E5E5;">With much appreciation,<br>Jordan</p>\n\n</div>\n</td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;">\n  <p style="margin:0 0 8px;font-size:13px;color:#999;">Questions? Reply to this email or visit <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;font-weight:600;">vibrationfit.com/support</a></p>\n  <p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p>\n</td></tr>\n\n</table>\n</td></tr>\n</table>\n</body>\n</html>',
  E'Hey {{firstName}},\n\nQuick heads up so there are no surprises.\n\nWhen you joined the 72-Hour Activation Intensive, you also unlocked 8 weeks of Vision Pro membership as part of that $1 founding tester offer.\n\nOn {{renewalDate}}, your membership is set to renew into {{planName}} at {{amount}} (billed once, then locked in for the year).\n\nThat renewal includes:\n- Full access to the Vision Builder and all creation tools\n- Unlimited vision boards, audios, journaling, and progress tracking\n- Your satisfaction guarantee window from the membership terms\n- Priority access to new features and future activations\n\nIf you''re loving the platform, you don''t need to do anything. Your membership will renew automatically on {{renewalDate}}.\n\nIf you don''t want to continue, no hard feelings at all.\nCancel or manage here: {{manageLink}}\n\nThank you again for being part of the first group inside VibrationFit.\n\nWith much appreciation,\nJordan',
  '["firstName", "renewalDate", "planName", "amount", "manageLink"]'::jsonb,
  '["Scheduled: 7 days before renewal", "Scheduled: 3 days before renewal"]'::jsonb
),

-- -------------------------------------------------------
-- 3. Access / Login Email (immediately after purchase)
-- -------------------------------------------------------
(
  'launch-access-login',
  'Launch: Access / Login Email',
  'Sent immediately after $1 purchase. Login link, password setup, 14-step overview.',
  'intensive',
  'active',
  E'You''re in: 72-Hour Activation Intensive access',
  E'<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <meta name="viewport" content="width=device-width, initial-scale=1.0">\n</head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;">\n<tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;">\n  <div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;">\n    <p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">72-Hour Activation Intensive</p>\n  </div>\n</td></tr>\n\n<tr><td style="padding:0;">\n<div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;text-align:center;line-height:1.2;">You''re In. Let''s activate.</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Your Activation Intensive access is live. Here''s everything you need to get started.</p>\n\n  <div style="margin:24px 0;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 8px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Your Login</p>\n    <p style="margin:0 0 16px;font-size:15px;color:#E5E5E5;">Your account is set up with <strong style="color:#fff;">{{email}}</strong></p>\n    <p style="margin:0 0 4px;font-size:15px;color:#E5E5E5;">1. Click the button below</p>\n    <p style="margin:0 0 4px;font-size:15px;color:#E5E5E5;">2. Set your password</p>\n    <p style="margin:0;font-size:15px;color:#E5E5E5;">3. You''ll land on your Intensive start page</p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:16px;"><tr><td align="center">\n    <a href="{{loginLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Set Your Password &amp; Log In &rarr;</a>\n  </td></tr></table>\n  <p style="margin:0 0 32px;font-size:13px;color:#999;text-align:center;">Already set your password? <a href="https://vibrationfit.com/auth/login" style="color:#39FF14;text-decoration:none;">Log in here</a></p>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">When Your 72 Hours Starts</p>\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">Your 72-hour focus window does <strong>not</strong> start until you click the "Start My Activation Intensive" button. No rush to click today if you need to carve out focused time first.</p>\n\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">Once you hit "Start," you''ll be guided through a clear <strong style="color:#39FF14;">14-step path</strong>:</p>\n\n  <div style="margin:0 0 24px;padding:20px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">1. Start Your Intensive</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">2. Account Settings</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">3. Baseline Intake</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">4. Create Your Profile</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">5. Vibration Assessment (84 Questions)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">6. Build Your Life Vision (12 Categories)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">7. Refine Your Vision with VIVA</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">8. Generate Your Activation Audio</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">9. Record Your Voice</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">10. Audio Mix</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">11. Build Your Vision Board</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">12. First Journal Entry</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">13. Book Your Calibration Call</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">14. My Activation Plan (28-Day Launch)</p>\n  </div>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">Most people complete everything inside their 72-hour window. If life happens and you need more time, take it. The timer is for momentum, not pressure.</p>\n\n  <div style="padding:20px;background-color:#000;border-radius:12px;border-left:4px solid #39FF14;">\n    <p style="margin:0 0 4px;font-size:15px;font-weight:600;color:#fff;">Need Help?</p>\n    <p style="margin:0;font-size:14px;line-height:1.6;color:#E5E5E5;">Just reply to this email or reach us at support@vibrationfit.com. We typically respond within 24 hours on business days.</p>\n  </div>\n\n</div>\n</td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;">\n  <p style="margin:0 0 8px;font-size:13px;color:#999;">Questions? Reply to this email or visit <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;font-weight:600;">vibrationfit.com/support</a></p>\n  <p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p>\n  <p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p>\n</td></tr>\n\n</table>\n</td></tr>\n</table>\n</body>\n</html>',
  E'YOU''RE IN: 72-HOUR ACTIVATION INTENSIVE ACCESS\n\nHi {{firstName}},\n\nYour Activation Intensive access is live. Here''s everything you need to get started.\n\nYOUR LOGIN\nYour account is set up with {{email}}\n\n1. Click the link below\n2. Set your password\n3. You''ll land on your Intensive start page\n\nSet Your Password & Log In: {{loginLink}}\n\nAlready set your password? Log in at https://vibrationfit.com/auth/login\n\nWHEN YOUR 72 HOURS STARTS\nYour 72-hour focus window does NOT start until you click the "Start My Activation Intensive" button. No rush today if you need to carve out time.\n\nOnce you hit "Start," you''ll follow a clear 14-step path:\n1. Start Your Intensive\n2. Account Settings\n3. Baseline Intake\n4. Create Your Profile\n5. Vibration Assessment (84 Questions)\n6. Build Your Life Vision (12 Categories)\n7. Refine Your Vision with VIVA\n8. Generate Your Activation Audio\n9. Record Your Voice\n10. Audio Mix\n11. Build Your Vision Board\n12. First Journal Entry\n13. Book Your Calibration Call\n14. My Activation Plan (28-Day Launch)\n\nMost people complete everything inside their 72-hour window. If life happens and you need more time, take it.\n\nNeed Help?\nReply to this email or reach us at support@vibrationfit.com.\n\nVibration Fit - Above the Green Line',
  '["firstName", "email", "loginLink"]'::jsonb,
  '["Stripe webhook: intensive purchase complete", "Immediately after $1 purchase"]'::jsonb
),

-- -------------------------------------------------------
-- 4. Orientation / Expectations Email (within 1 hour)
-- -------------------------------------------------------
(
  'launch-orientation',
  'Launch: Orientation / Expectations',
  'Frames the journey, reduces buyer remorse. Sent within 1 hour of purchase.',
  'intensive',
  'active',
  'What to expect inside your Activation Intensive',
  E'<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <meta name="viewport" content="width=device-width, initial-scale=1.0">\n</head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;">\n<tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;">\n  <div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;">\n    <p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Orientation</p>\n  </div>\n</td></tr>\n\n<tr><td style="padding:0;">\n<div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Here''s how this works</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">You''re inside the 72-Hour Activation Intensive. Here''s what that actually means and what to focus on first.</p>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">What you''re building</p>\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">The Intensive is a 14-step guided path. By the end, you''ll have:</p>\n  <div style="margin:0 0 24px;padding:20px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your Life Vision written across 12 categories, built with VIVA</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A personalized Activation Audio (morning + evening)</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A Vision Board with images for each category</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your first Conscious Creation Journal entry</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A live Calibration Call booked</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A 28-Day Activation Plan to make it your daily rhythm</p>\n  </div>\n  <p style="margin:0 0 32px;font-size:15px;line-height:1.6;color:#E5E5E5;">That''s what "done" looks like. Not "I read about it." You''ll have a living system drafted, recorded, visualized, and scheduled.</p>\n\n  <div style="margin:0 0 32px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Your first 24 hours: 3 things</p>\n    <p style="margin:0 0 12px;font-size:15px;color:#E5E5E5;"><strong style="color:#39FF14;">1.</strong> <strong style="color:#fff;">Log in and set your password</strong><br><span style="font-size:13px;color:#999;">If you haven''t already, this takes 30 seconds.</span></p>\n    <p style="margin:0 0 12px;font-size:15px;color:#E5E5E5;"><strong style="color:#39FF14;">2.</strong> <strong style="color:#fff;">Start your Intensive</strong><br><span style="font-size:13px;color:#999;">Click "Start My Activation Intensive" on your dashboard. This begins your 72-hour focus window.</span></p>\n    <p style="margin:0;font-size:15px;color:#E5E5E5;"><strong style="color:#39FF14;">3.</strong> <strong style="color:#fff;">Complete Steps 1-4</strong><br><span style="font-size:13px;color:#999;">Account, Intake, Profile, Assessment. Plan ~60-90 minutes for this first session.</span></p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:32px;"><tr><td align="center">\n    <a href="https://vibrationfit.com/auth/login" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Log In &rarr;</a>\n  </td></tr></table>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #333;">\n    <p style="margin:0 0 8px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Your $1 Founding Tester Access</p>\n    <p style="margin:0 0 12px;font-size:15px;line-height:1.6;color:#E5E5E5;">You paid $1 today to verify your payment method. Here''s exactly what that means:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;"><strong style="color:#fff;">What you have right now:</strong> Full access to the 72-Hour Activation Intensive + 8 weeks of Vision Pro. Same platform, same tools, same VIVA &ndash; nothing is held back.</p>\n    <p style="margin:12px 0 4px;font-size:14px;color:#E5E5E5;"><strong style="color:#fff;">What happens on Day 56:</strong></p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Annual: $999/year (~$76.85 per 28-day cycle)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; 28-Day: $99, renews every 28 days</p>\n    <p style="margin:12px 0 0;font-size:14px;color:#E5E5E5;"><strong style="color:#fff;">If you want to change or cancel:</strong> You can switch plans or cancel anytime before Day 56. No charge beyond your $1. Manage from Account Settings.</p>\n  </div>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;text-align:center;font-weight:600;">No surprises. No fine print.</p>\n\n</div>\n</td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;">\n  <p style="margin:0 0 8px;font-size:13px;color:#999;">Questions? Reach us at <a href="mailto:team@vibrationfit.com" style="color:#39FF14;text-decoration:none;font-weight:600;">team@vibrationfit.com</a></p>\n  <p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p>\n  <p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p>\n</td></tr>\n\n</table>\n</td></tr>\n</table>\n</body>\n</html>',
  E'WHAT TO EXPECT INSIDE YOUR ACTIVATION INTENSIVE\n\nHi {{firstName}},\n\nYou''re inside the 72-Hour Activation Intensive. Here''s what that actually means.\n\nWHAT YOU''RE BUILDING\nThe Intensive is a 14-step guided path. By the end, you''ll have:\n- Your Life Vision written across 12 categories, built with VIVA\n- A personalized Activation Audio (morning + evening)\n- A Vision Board with images for each category\n- Your first Conscious Creation Journal entry\n- A live Calibration Call booked\n- A 28-Day Activation Plan to make it your daily rhythm\n\nYOUR FIRST 24 HOURS: 3 THINGS\n1. Log in and set your password\n2. Start your Intensive (click "Start My Activation Intensive")\n3. Complete Steps 1-4 (Account, Intake, Profile, Assessment) - plan ~60-90 min\n\nLog in: https://vibrationfit.com/auth/login\n\nYOUR $1 FOUNDING TESTER ACCESS\nYou paid $1 to verify your payment. Here''s the deal:\n- Right now: Full access to Intensive + 8 weeks Vision Pro\n- Day 56: Annual $999/year or 28-Day $99\n- Cancel anytime before Day 56 from Account Settings\n\nNo surprises. No fine print.\n\nQuestions? team@vibrationfit.com\n\nVibration Fit - Above the Green Line',
  '["firstName"]'::jsonb,
  '["Scheduled: 1 hour after purchase"]'::jsonb
),

