INSERT INTO sms_templates (slug, name, description, category, status, body, variables, triggers)
VALUES
  (
    'launch-pre-renewal-3day-sms',
    'Launch: Pre-Renewal SMS (3 days)',
    '3-day renewal reminder via SMS',
    'reminders',
    'active',
    'Vibration Fit reminder: your {{planName}} renews on {{renewalDate}} for {{planAmount}}. To change or cancel before then, go to: {{manageLink}}',
    '["planName", "renewalDate", "planAmount", "manageLink"]'::jsonb,
    '["Scheduled: 3 days before renewal"]'::jsonb
  ),
  (
    'launch-pre-renewal-1day-sms',
    'Launch: Pre-Renewal SMS (1 day)',
    'Final day renewal reminder via SMS',
    'reminders',
    'active',
    'Final reminder: your Vibration Fit {{planName}} renews tomorrow for {{planAmount}}. To change or cancel before it runs, visit: {{manageLink}}',
    '["planName", "planAmount", "manageLink"]'::jsonb,
    '["Scheduled: 1 day before renewal"]'::jsonb
  ),
  (
    'launch-dunning-sms',
    'Launch: Dunning SMS',
    'SMS notification for failed payment (send on attempt 2 or 3)',
    'billing',
    'active',
    'Vibration Fit: your {{planName}} payment for {{planAmount}} failed. Update your card to avoid an access pause: {{updateLink}}',
    '["planName", "planAmount", "updateLink"]'::jsonb,
    '["Stripe: payment failed (attempt 2 or 3)"]'::jsonb
  )
ON CONFLICT (slug) DO UPDATE SET
  name = EXCLUDED.name, description = EXCLUDED.description, category = EXCLUDED.category,
  status = EXCLUDED.status, body = EXCLUDED.body, variables = EXCLUDED.variables,
  triggers = EXCLUDED.triggers, updated_at = NOW();


