INSERT INTO email_templates (slug, name, description, category, status, subject, html_body, text_body, variables, triggers)
VALUES

-- -------------------------------------------------------
-- Pre-Renewal 7 Day
-- -------------------------------------------------------
(
  'launch-pre-renewal-7day',
  'Launch: Pre-Renewal (7 days)',
  'Value recap + what happens next. 7 days before renewal.',
  'billing',
  'active',
  '7 days until your Vibration Fit membership renews',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Membership Update</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">7 days until renewal</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">You''re coming up on the end of your 8-week Founding Tester window inside Vibration Fit. I want to make sure you know exactly what happens next.</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">What you''ve unlocked so far</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> The 72-Hour Activation Intensive</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your Life Vision, Activation Audio, and Vision Board</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your 28-Day Activation Plan and main dashboard</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Ongoing access to Vision Pro tools</p>\n  </div>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 8px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">What happens in 7 days</p>\n    <p style="margin:0 0 12px;font-size:15px;color:#E5E5E5;">On <strong style="color:#fff;">{{renewalDate}}</strong>, your membership is set to continue automatically:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; If you chose Annual: $999/year (billed once per year)</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; If you chose 28-Day: $99 every 28 days</p>\n  </div>\n\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you''re getting value and want to stay on your journey, you don''t need to do anything. Your membership will renew and your rate will be locked in.</p>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you want to change plans or cancel before then:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Manage My Membership</a></td></tr></table>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;text-align:center;">No surprises. No fine print.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">With appreciation for being a $1 Founding Tester,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'7 DAYS UNTIL YOUR VIBRATION FIT MEMBERSHIP RENEWS\n\nHi {{firstName}},\n\nYou''re coming up on the end of your 8-week Founding Tester window inside Vibration Fit.\n\nWHAT YOU''VE UNLOCKED SO FAR\n- The 72-Hour Activation Intensive\n- Your Life Vision, Activation Audio, and Vision Board\n- Your 28-Day Activation Plan and main dashboard\n- Ongoing access to Vision Pro tools\n\nWHAT HAPPENS IN 7 DAYS\nOn {{renewalDate}}, your membership continues automatically:\n- Annual: $999/year (billed once per year)\n- 28-Day: $99 every 28 days\n\nIf you''re getting value, you don''t need to do anything.\n\nTo change plans or cancel: {{manageLink}}\n\nNo surprises. No fine print.\n\nWith appreciation,\nThe Vibration Fit Team',
  '["firstName", "renewalDate", "manageLink"]'::jsonb,
  '["Scheduled: 7 days before renewal"]'::jsonb
),

-- -------------------------------------------------------
-- Pre-Renewal 3 Day
-- -------------------------------------------------------
(
  'launch-pre-renewal-3day',
  'Launch: Pre-Renewal (3 days)',
  'Clear reminder with date, amount, and how to change or cancel. 3 days before renewal.',
  'billing',
  'active',
  '3-day reminder: your Vibration Fit membership renewal',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">3-Day Reminder</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Quick reminder so there are no surprises.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">On <strong style="color:#39FF14;">{{renewalDate}}</strong> (in 3 days), your Vibration Fit membership is scheduled to renew:</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <table width="100%" cellpadding="0" cellspacing="0">\n      <tr><td style="padding:8px 0;font-size:14px;color:#999;width:120px;">Plan</td><td style="padding:8px 0;font-size:16px;color:#fff;font-weight:600;">{{planName}}</td></tr>\n      <tr><td style="padding:8px 0;font-size:14px;color:#999;">Amount</td><td style="padding:8px 0;font-size:16px;color:#39FF14;font-weight:600;">{{planAmount}}</td></tr>\n      <tr><td style="padding:8px 0;font-size:14px;color:#999;">Schedule</td><td style="padding:8px 0;font-size:14px;color:#E5E5E5;">{{planSchedule}}</td></tr>\n    </table>\n  </div>\n\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you''re loving the platform and want to continue, you''re all set. The renewal will process automatically and your access continues without interruption.</p>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you''d rather change plans or cancel before it renews:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Manage My Membership</a></td></tr></table>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">You''ve put real effort into activating your vision. Whether you stay or go, I want this to feel clean and transparent.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">With appreciation,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'3-DAY REMINDER: YOUR VIBRATION FIT MEMBERSHIP RENEWAL\n\nHi {{firstName}},\n\nQuick reminder so there are no surprises.\n\nOn {{renewalDate}} (in 3 days), your membership renews:\n\nPlan: {{planName}}\nAmount: {{planAmount}}\nSchedule: {{planSchedule}}\n\nIf you''re loving the platform, you''re all set.\n\nTo change plans or cancel: {{manageLink}}\n\nWith appreciation,\nThe Vibration Fit Team',
  '["firstName", "renewalDate", "planName", "planAmount", "planSchedule", "manageLink"]'::jsonb,
  '["Scheduled: 3 days before renewal"]'::jsonb
),

-- -------------------------------------------------------
-- Pre-Renewal 1 Day
-- -------------------------------------------------------
(
  'launch-pre-renewal-1day',
  'Launch: Pre-Renewal (1 day)',
  'Final polite heads-up. Last chance to cancel before charge. 1 day before renewal.',
  'billing',
  'active',
  'Tomorrow: your Vibration Fit membership renewal',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Final Reminder</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Final quick heads-up:</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Tomorrow, <strong style="color:#39FF14;">{{renewalDate}}</strong>, your Vibration Fit membership is set to renew:</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <table width="100%" cellpadding="0" cellspacing="0">\n      <tr><td style="padding:8px 0;font-size:14px;color:#999;width:120px;">Plan</td><td style="padding:8px 0;font-size:16px;color:#fff;font-weight:600;">{{planName}}</td></tr>\n      <tr><td style="padding:8px 0;font-size:14px;color:#999;">Amount</td><td style="padding:8px 0;font-size:16px;color:#39FF14;font-weight:600;">{{planAmount}}</td></tr>\n    </table>\n  </div>\n\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you''re continuing, you''re all set &ndash; nothing else to do.</p>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you <strong>do not</strong> want to continue, this is your last chance to change or cancel before the charge goes through:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Manage My Membership</a></td></tr></table>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">Thank you again for being part of the first group inside Vibration Fit.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">To making history,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'TOMORROW: YOUR VIBRATION FIT MEMBERSHIP RENEWAL\n\nHi {{firstName}},\n\nFinal quick heads-up:\n\nTomorrow, {{renewalDate}}, your Vibration Fit membership renews:\n\nPlan: {{planName}}\nAmount: {{planAmount}}\n\nIf you''re continuing, you''re all set.\n\nIf you do NOT want to continue, this is your last chance:\n{{manageLink}}\n\nTo making history,\nThe Vibration Fit Team',
  '["firstName", "renewalDate", "planName", "planAmount", "manageLink"]'::jsonb,
  '["Scheduled: 1 day before renewal"]'::jsonb
),

-- -------------------------------------------------------
-- Renewal Confirmation / Receipt
-- -------------------------------------------------------
(
  'launch-renewal-confirmation',
  'Launch: Renewal Confirmation / Receipt',
  'Confirmation that membership renewed. Receipt details, value re-anchor, manage link.',
  'billing',
  'active',
  'Your Vibration Fit membership renewed today',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Renewal Receipt</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Your Vibration Fit membership renewed today. Here are the details for your records:</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <table width="100%" cellpadding="0" cellspacing="0">\n      <tr><td style="padding:8px 0;border-bottom:1px solid #333;font-size:14px;color:#999;width:140px;">Plan</td><td style="padding:8px 0;border-bottom:1px solid #333;font-size:16px;color:#fff;font-weight:600;">{{planName}}</td></tr>\n      <tr><td style="padding:8px 0;border-bottom:1px solid #333;font-size:14px;color:#999;">Amount charged</td><td style="padding:8px 0;border-bottom:1px solid #333;font-size:16px;color:#39FF14;font-weight:600;">{{planAmount}}</td></tr>\n      <tr><td style="padding:8px 0;border-bottom:1px solid #333;font-size:14px;color:#999;">Billing schedule</td><td style="padding:8px 0;border-bottom:1px solid #333;font-size:14px;color:#E5E5E5;">{{planSchedule}}</td></tr>\n      <tr><td style="padding:8px 0;border-bottom:1px solid #333;font-size:14px;color:#999;">Next billing date</td><td style="padding:8px 0;border-bottom:1px solid #333;font-size:14px;color:#E5E5E5;">{{nextBillingDate}}</td></tr>\n      <tr><td style="padding:8px 0;font-size:14px;color:#999;">Card ending in</td><td style="padding:8px 0;font-size:14px;color:#E5E5E5;">{{cardLast4}}</td></tr>\n    </table>\n  </div>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">What your membership includes</p>\n  <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your Life Vision, Activation Audios, and Vision Board</p>\n  <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your MAP (My Activation Plan) and main dashboard</p>\n  <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Access to the Vibe Tribe community</p>\n  <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Weekly live coaching calls at the Alignment Gym</p>\n  <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Tools to keep iterating your vision and vibration</p>\n  <p style="margin:0 0 24px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Upcoming feature updates and future activations</p>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">This isn''t just access to software; it''s your environment for consciously creating and refining the life you''re moving toward.</p>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">Need to update anything?</p>\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:16px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Manage My Membership</a></td></tr></table>\n  <p style="margin:0 0 24px;font-size:13px;color:#999;text-align:center;">Update payment, change plans, or cancel future renewals</p>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">If anything about this charge looks off, go to <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a> while logged in.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">Thank you for continuing to build your vision with us,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'YOUR VIBRATION FIT MEMBERSHIP RENEWED TODAY\n\nHi {{firstName}},\n\nYour Vibration Fit membership renewed today.\n\nPlan: {{planName}}\nAmount charged: {{planAmount}}\nBilling schedule: {{planSchedule}}\nNext billing date: {{nextBillingDate}}\nCard ending in: {{cardLast4}}\n\nWHAT YOUR MEMBERSHIP INCLUDES\n- Your Life Vision, Activation Audios, and Vision Board\n- Your MAP and main dashboard\n- Access to the Vibe Tribe community\n- Weekly live coaching calls at the Alignment Gym\n- Tools to keep iterating your vision and vibration\n- Upcoming feature updates and future activations\n\nManage your membership: {{manageLink}}\n\nThank you for continuing to build your vision with us,\nThe Vibration Fit Team',
  '["firstName", "planName", "planAmount", "planSchedule", "nextBillingDate", "cardLast4", "manageLink"]'::jsonb,
  '["Stripe webhook: subscription renewed successfully"]'::jsonb
),

-- -------------------------------------------------------
-- Dunning Attempt 1 - Friendly
-- -------------------------------------------------------
(
  'launch-dunning-attempt1',
  'Launch: Failed Payment (Attempt 1)',
  'Friendly card issue notification. No threat, just a quick fix link.',
  'billing',
  'active',
  E'Quick fix: your Vibration Fit payment didn''t go through',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(255,183,1,0.1);border-radius:50px;border:2px solid #FFB701;"><p style="margin:0;font-size:12px;font-weight:600;color:#FFB701;text-transform:uppercase;letter-spacing:1px;">Payment Issue</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #FFB701;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">Heads up: your recent Vibration Fit <strong style="color:#fff;">{{planName}}</strong> payment for <strong style="color:#FFB701;">{{planAmount}}</strong> didn''t go through.</p>\n\n  <p style="margin:0 0 8px;font-size:15px;line-height:1.6;color:#E5E5E5;">This usually happens because of:</p>\n  <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; An expired card</p>\n  <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; A new card issued by your bank</p>\n  <p style="margin:0 0 24px;font-size:14px;color:#E5E5E5;">&#8226; A temporary bank hold</p>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;"><strong style="color:#39FF14;">You still have full access right now.</strong> To keep everything running smoothly, please update your payment method:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:16px;"><tr><td align="center"><a href="{{updateLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Update My Payment Details</a></td></tr></table>\n  <p style="margin:0 0 24px;font-size:13px;color:#999;text-align:center;">Takes about 30 seconds.</p>\n\n  <p style="margin:0;font-size:14px;line-height:1.6;color:#999;">If you believe this is an error, go to <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a> while logged in.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">Thank you for continuing to build your vision with us,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'QUICK FIX: YOUR VIBRATION FIT PAYMENT DIDN''T GO THROUGH\n\nHi {{firstName}},\n\nHeads up: your Vibration Fit {{planName}} payment for {{planAmount}} didn''t go through.\n\nThis usually happens because of an expired card, a new card from your bank, or a temporary hold.\n\nYou still have full access right now. Update your payment method here:\n{{updateLink}}\n\nTakes about 30 seconds.\n\nThe Vibration Fit Team',
  '["firstName", "planName", "planAmount", "updateLink"]'::jsonb,
  '["Stripe webhook: payment_intent.payment_failed (attempt 1)"]'::jsonb
),

-- -------------------------------------------------------
-- Dunning Attempt 2 - Clearer, softer deadline
-- -------------------------------------------------------
(
  'launch-dunning-attempt2',
  'Launch: Failed Payment (Attempt 2)',
  'Second attempt. Clearer with pause date. Offers manage link for intentional cancelers.',
  'billing',
  'active',
  'Action needed to keep your Vibration Fit access',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(255,183,1,0.1);border-radius:50px;border:2px solid #FFB701;"><p style="margin:0;font-size:12px;font-weight:600;color:#FFB701;text-transform:uppercase;letter-spacing:1px;">Action Needed</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #FFB701;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">We tried to process your Vibration Fit <strong style="color:#fff;">{{planName}}</strong> payment for <strong style="color:#FFB701;">{{planAmount}}</strong> again, and it still didn''t go through.</p>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">To avoid any interruption to your access, please update your payment details:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{updateLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Update My Payment Details</a></td></tr></table>\n\n  <div style="padding:16px;background-color:#000;border-radius:12px;border:2px solid #FFB701;margin-bottom:24px;">\n    <p style="margin:0;font-size:14px;color:#FFB701;text-align:center;font-weight:600;">If not updated by {{pauseDate}}, we''ll need to temporarily pause your account.</p>\n  </div>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you''re intentionally trying to cancel or downgrade, you can manage your membership here instead:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:14px 36px;background-color:transparent;color:#39FF14;text-decoration:none;font-size:14px;font-weight:600;border-radius:50px;border:2px solid #39FF14;">Manage My Membership</a></td></tr></table>\n\n  <p style="margin:0;font-size:14px;line-height:1.6;color:#999;">If something doesn''t look right, go to <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a> while logged in.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">With appreciation,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'ACTION NEEDED TO KEEP YOUR VIBRATION FIT ACCESS\n\nHi {{firstName}},\n\nWe tried to process your {{planName}} payment for {{planAmount}} again, and it still didn''t go through.\n\nUpdate your payment details: {{updateLink}}\n\nIf not updated by {{pauseDate}}, we''ll need to temporarily pause your account.\n\nIf you''re trying to cancel, manage your membership here: {{manageLink}}\n\nWith appreciation,\nThe Vibration Fit Team',
  '["firstName", "planName", "planAmount", "updateLink", "pauseDate", "manageLink"]'::jsonb,
  '["Stripe webhook: payment_intent.payment_failed (attempt 2, 2-3 days after first fail)"]'::jsonb
),

-- -------------------------------------------------------
-- Dunning Attempt 3 - Final notice
-- -------------------------------------------------------
(
  'launch-dunning-attempt3',
  'Launch: Failed Payment (Attempt 3 - Final)',
  'Final notice before access pause. Firm but fair.',
  'billing',
  'active',
  'Final notice: your Vibration Fit access will pause on {{pauseDate}}',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(255,0,64,0.1);border-radius:50px;border:2px solid #FF0040;"><p style="margin:0;font-size:12px;font-weight:600;color:#FF0040;text-transform:uppercase;letter-spacing:1px;">Final Notice</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #FF0040;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">This is your final reminder about your Vibration Fit <strong style="color:#fff;">{{planName}}</strong> payment for <strong style="color:#FF0040;">{{planAmount}}</strong>.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">We still haven''t been able to process your payment. Unless you update your payment method, <strong style="color:#FF0040;">your account will be paused on {{pauseDate}}</strong>.</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{updateLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Update My Payment Details</a></td></tr></table>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you no longer wish to continue, you can also change plans or cancel here:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:14px 36px;background-color:transparent;color:#39FF14;text-decoration:none;font-size:14px;font-weight:600;border-radius:50px;border:2px solid #39FF14;">Manage My Membership</a></td></tr></table>\n\n  <p style="margin:0;font-size:14px;line-height:1.6;color:#999;">If you think this is a mistake, visit <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a> while logged in and we''ll sort it out.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">Thank you for being part of the Vibe Tribe,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'FINAL NOTICE: YOUR VIBRATION FIT ACCESS WILL PAUSE ON {{pauseDate}}\n\nHi {{firstName}},\n\nThis is your final reminder about your {{planName}} payment for {{planAmount}}.\n\nWe still haven''t been able to process your payment. Unless you update your payment method, your account will be paused on {{pauseDate}}.\n\nUpdate payment: {{updateLink}}\n\nTo change plans or cancel: {{manageLink}}\n\nThank you for being part of the Vibe Tribe,\nThe Vibration Fit Team',
  '["firstName", "planName", "planAmount", "updateLink", "pauseDate", "manageLink"]'::jsonb,
  '["Stripe webhook: payment_intent.payment_failed (attempt 3, right before pause)"]'::jsonb
),

-- -------------------------------------------------------
-- Cancellation Confirmation
-- -------------------------------------------------------
(
  'launch-cancellation-confirmation',
  'Launch: Cancellation Confirmation',
  'Clear, respectful cancellation confirmation. What they lose, exit survey link, reactivate option.',
  'billing',
  'active',
  'Your Vibration Fit membership has been canceled',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(255,0,64,0.1);border-radius:50px;border:2px solid #FF0040;"><p style="margin:0;font-size:12px;font-weight:600;color:#FF0040;text-transform:uppercase;letter-spacing:1px;">Membership Canceled</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #333;">\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 16px;font-size:16px;line-height:1.6;color:#E5E5E5;">Your Vibration Fit <strong style="color:#fff;">{{planName}}</strong> membership has been canceled effective <strong style="color:#fff;">{{endDate}}</strong>.</p>\n\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">You''ll keep full access until that date. After {{endDate}}, you''ll lose access to:</p>\n\n  <div style="margin:0 0 24px;padding:20px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;">&#8226; Your main Vibration Fit dashboard</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;">&#8226; Your Life Vision, Activation Audios, and Vision Board inside the app</p>\n    <p style="margin:0 0 6px;font-size:14px;color:#E5E5E5;">&#8226; Your MAP (My Activation Plan) and progress tracking</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; Any future member-only updates, features, or activations</p>\n  </div>\n\n  <p style="margin:0 0 24px;font-size:14px;color:#999;">There are no additional charges scheduled after {{endDate}} for this membership.</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#39FF14;">Quick 30-second exit check-in</p>\n    <p style="margin:0 0 16px;font-size:14px;line-height:1.6;color:#E5E5E5;">If you''re open to it, I''d really appreciate hearing why you chose to cancel so we can improve the experience for future members. You can be blunt.</p>\n    <table width="100%" cellpadding="0" cellspacing="0"><tr><td align="center"><a href="{{exitSurveyLink}}" style="display:inline-block;padding:14px 36px;background-color:transparent;color:#39FF14;text-decoration:none;font-size:14px;font-weight:600;border-radius:50px;border:2px solid #39FF14;">Share Quick Feedback (30 seconds)</a></td></tr></table>\n  </div>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">If this was a mistake</p>\n  <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">If you didn''t intend to cancel or change your mind, you can reactivate or change plans here:</p>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{manageLink}}" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Manage My Membership</a></td></tr></table>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">If something looks off, go to <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a> while logged in and we''ll sort it out.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0 0 8px;font-size:15px;line-height:1.6;color:#E5E5E5;">Thank you for giving Vibration Fit a shot and for the work you did inside the platform. We all appreciate you and wish you all the best!</p>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">Sending love and light,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'YOUR VIBRATION FIT MEMBERSHIP HAS BEEN CANCELED\n\nHi {{firstName}},\n\nYour Vibration Fit {{planName}} membership has been canceled effective {{endDate}}.\n\nYou''ll keep full access until that date. After {{endDate}}, you''ll lose access to:\n- Your main Vibration Fit dashboard\n- Your Life Vision, Activation Audios, and Vision Board\n- Your MAP and progress tracking\n- Any future member-only updates and activations\n\nNo additional charges after {{endDate}}.\n\nQUICK 30-SECOND EXIT CHECK-IN\nWe''d appreciate hearing why you canceled: {{exitSurveyLink}}\n\nIF THIS WAS A MISTAKE\nReactivate or change plans: {{manageLink}}\n\nThank you for giving Vibration Fit a shot. We wish you all the best!\n\nSending love and light,\nThe Vibration Fit Team',
  '["firstName", "planName", "endDate", "exitSurveyLink", "manageLink"]'::jsonb,
  '["Stripe webhook: subscription canceled"]'::jsonb
)

ON CONFLICT (slug) DO UPDATE SET
  name = EXCLUDED.name, description = EXCLUDED.description, category = EXCLUDED.category,
  status = EXCLUDED.status, subject = EXCLUDED.subject, html_body = EXCLUDED.html_body,
  text_body = EXCLUDED.text_body, variables = EXCLUDED.variables,
  triggers = EXCLUDED.triggers, updated_at = NOW();
