-- and creates the Intensive Onboarding drip sequence with skip conditions.
-- ============================================================================


-- ============================================================================
-- 1. SMS TEMPLATES
-- ============================================================================

INSERT INTO sms_templates (slug, name, description, category, status, body, variables, triggers)
VALUES
  (
    'launch-friends-family-sms-initial',
    'Launch: Friends & Family SMS (Initial)',
    'Initial outreach text for founding tester group. Manual send.',
    'marketing',
    'active',
    E'Hey! We just finished building our Vibration Fit platform and I''m running a tiny "friends & family" test of our 72-Hour Activation Intensive. It''ll be $499 when we launch, but I''m giving this first round for $1, and it also includes 8 weeks of membership starting when you join. All I''m asking for is honest feedback (and a testimonial if you love it). Want me to send you the link?',
    '["name"]'::jsonb,
    '["Manual send from CRM"]'::jsonb
  ),
  (
    'launch-friends-family-sms-yes',
    'Launch: Friends & Family SMS (Yes Reply)',
    'Follow-up with promo link after they say yes. Manual send.',
    'marketing',
    'active',
    E'Amazing, thank you! Here''s the $1 founding tester link for the 72-Hour Activation Intensive + 8 weeks of membership: https://vibrationfit.com/?promo=LAUNCH2026\n\nWe''re only taking about 15-20 people in this first round, so grab it when you can.',
    '["name"]'::jsonb,
    '["Manual send from CRM after positive reply"]'::jsonb
  ),
  (
    'launch-pre-renewal-headsup-sms',
    'Launch: Pre-Renewal Heads-Up SMS',
    'General renewal heads-up 3-5 days before Day 56 renewal.',
    'billing',
    'active',
    E'Hey {{firstName}}, quick reminder: your Vibration Fit Vision Pro membership is set to renew on {{renewalDate}} at $999 for 12 months. If you''re loving it, you''re all set. If you''d rather cancel before it renews, you can do that here: {{manageLink}}. Just want to make sure there are no surprises.',
    '["firstName", "renewalDate", "manageLink"]'::jsonb,
    '["Scheduled: 3-5 days before Day 56 renewal"]'::jsonb
  )
ON CONFLICT (slug) DO UPDATE SET
  name = EXCLUDED.name, description = EXCLUDED.description, category = EXCLUDED.category,
  status = EXCLUDED.status, body = EXCLUDED.body, variables = EXCLUDED.variables,
  triggers = EXCLUDED.triggers, updated_at = NOW();


