INSERT INTO email_templates (slug, name, description, category, status, subject, html_body, text_body, variables, triggers)
VALUES

-- -------------------------------------------------------
-- Bucket 2: Welcome / Access Email (immediately after purchase)
-- -------------------------------------------------------
(
  'intensive-welcome-access',
  'Intensive: Welcome & Access',
  'Immediately after purchase. Login link, password setup, 14-step overview, 72-hour framing.',
  'intensive',
  'active',
  E'You''re in: 72-Hour Activation Intensive access',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">72-Hour Activation Intensive</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;text-align:center;line-height:1.2;">You''re In. Let''s activate.</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Your Activation Intensive access is live. Here''s everything you need to get started.</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Your Login</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#E5E5E5;">Your account is set up with <strong style="color:#fff;">{{email}}</strong></p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">1. Click the button below</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">2. Set your password</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">3. You''ll land on your Intensive start page</p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:16px;"><tr><td align="center"><a href="https://vibrationfit.com/auth/setup-password?intensive=true" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Set Your Password &amp; Log In</a></td></tr></table>\n  <p style="margin:0 0 24px;font-size:13px;color:#999;text-align:center;">Already set your password? <a href="https://vibrationfit.com/auth/login" style="color:#39FF14;text-decoration:none;">Log in here</a></p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">When Your 72 Hours Starts</p>\n    <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">Your 72-hour focus window does <strong>not</strong> start until you click the "Start My Activation Intensive" button. No rush to click today if you need to carve out focused time first.</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;">Once you hit "Start," you''ll be guided through a clear 14-step path:</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">1. Start Your Intensive</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">2. Account Settings</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">3. Baseline Intake</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">4. Create Your Profile</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">5. Vibration Assessment (84 Questions)</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">6. Build Your Life Vision (12 Categories)</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">7. Refine Your Vision with VIVA</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">8. Generate Your Activation Audio</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">9. Record Your Voice</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">10. Audio Mix</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">11. Build Your Vision Board</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">12. First Journal Entry</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">13. Book Your Calibration Call</p>\n    <p style="margin:0;font-size:13px;color:#E5E5E5;">14. My Activation Plan (28-Day Launch)</p>\n  </div>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">Most people complete everything inside their 72-hour window. If life happens and you need more time, take it. The timer is for momentum, not pressure.</p>\n\n  <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">Need Help?</p>\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">Just reply to this email or reach us at <a href="mailto:support@vibrationfit.com" style="color:#39FF14;text-decoration:none;">support@vibrationfit.com</a>. We typically respond within 24 hours on business days.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">Welcome aboard,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 12px;font-size:13px;color:#999;">Questions? Reply to this email or visit <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;font-weight:600;">vibrationfit.com/support</a></p><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'YOU''RE IN: 72-HOUR ACTIVATION INTENSIVE ACCESS\n\nHi {{firstName}},\n\nYour Activation Intensive access is live. Here''s everything you need to get started.\n\nYOUR LOGIN\nYour account is set up with {{email}}\n1. Click the link below\n2. Set your password\n3. You''ll land on your Intensive start page\n\nSet Your Password & Log In:\nhttps://vibrationfit.com/auth/setup-password?intensive=true\n\nAlready set your password? Log in here:\nhttps://vibrationfit.com/auth/login\n\nWHEN YOUR 72 HOURS STARTS\nYour 72-hour focus window does NOT start until you click the "Start My Activation Intensive" button. No rush to click today if you need to carve out focused time first.\n\nOnce you hit "Start," you''ll be guided through a clear 14-step path:\n1. Start Your Intensive\n2. Account Settings\n3. Baseline Intake\n4. Create Your Profile\n5. Vibration Assessment (84 Questions)\n6. Build Your Life Vision (12 Categories)\n7. Refine Your Vision with VIVA\n8. Generate Your Activation Audio\n9. Record Your Voice\n10. Audio Mix\n11. Build Your Vision Board\n12. First Journal Entry\n13. Book Your Calibration Call\n14. My Activation Plan (28-Day Launch)\n\nMost people complete everything inside their 72-hour window. If life happens and you need more time, take it. The timer is for momentum, not pressure.\n\nNEED HELP?\nJust reply to this email or reach us at support@vibrationfit.com.\nWe typically respond within 24 hours on business days.\n\nWelcome aboard,\nThe Vibration Fit Team',
  '["firstName", "email"]'::jsonb,
  '["Stripe webhook: intensive purchased", "triggerEvent: intensive.purchased"]'::jsonb
),

-- -------------------------------------------------------
-- Bucket 3: Orientation / Expectations (within 1 hour)
-- -------------------------------------------------------
(
  'intensive-orientation',
  'Intensive: Orientation & Expectations',
  'Sent ~1 hour after purchase. What success looks like, 3 things for first 24 hours, $1 tester details.',
  'intensive',
  'active',
  'What to expect inside your Activation Intensive',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Orientation</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Here''s how this works</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">You''re inside the 72-Hour Activation Intensive. Here''s what that actually means and what to focus on first.</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">What you''re building</p>\n    <p style="margin:0 0 8px;font-size:15px;line-height:1.6;color:#E5E5E5;">The Intensive is a 14-step guided path. By the end, you''ll have:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your Life Vision written across 12 categories, built with VIVA</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A personalized Activation Audio (morning + evening)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A Vision Board with images for each category</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> Your first Conscious Creation Journal entry</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A live Calibration Call booked</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;"><span style="color:#39FF14;">&#10003;</span> A 28-Day Activation Plan to make it your daily rhythm</p>\n  </div>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 16px;font-size:16px;font-weight:600;color:#fff;">Your first 24 hours: 3 things</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">1. Log in and set your password</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">If you haven''t already, this takes 30 seconds.</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">2. Start your Intensive</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">Click "Start My Activation Intensive" on your dashboard. This begins your 72-hour focus window.</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">3. Complete Steps 1-4</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">Account, Intake, Profile, Assessment. These are your foundation. Plan on one focused session (about 60-90 minutes).</p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="https://vibrationfit.com/auth/login" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Log In</a></td></tr></table>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">Your $1 Founding Tester access</p>\n    <p style="margin:0 0 16px;font-size:15px;line-height:1.6;color:#E5E5E5;">You paid $1 today to verify your payment method. Here''s exactly what that means:</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;"><strong style="color:#39FF14;">What you have right now:</strong> Full access to the 72-Hour Activation Intensive + 8 weeks of Vision Pro. Same platform, same tools, same VIVA. Nothing is held back.</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;"><strong style="color:#39FF14;">What happens on Day 56:</strong> Your selected plan begins automatically:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Annual: $999/year (about $76.85 per 28-day cycle)</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">&#8226; 28-Day: $99, renews every 28 days</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">You can switch plans or cancel anytime before Day 56. No charge beyond your $1. Manage this from your Account Settings.</p>\n  </div>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">No surprises. No fine print.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:14px;line-height:1.6;color:#999;">Questions about your account or billing? Reach us anytime at <a href="mailto:team@vibrationfit.com" style="color:#39FF14;text-decoration:none;">team@vibrationfit.com</a></p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'WHAT TO EXPECT INSIDE YOUR ACTIVATION INTENSIVE\n\nHi {{firstName}},\n\nYou''re inside the 72-Hour Activation Intensive. Here''s what that actually means and what to focus on first.\n\nWHAT YOU''RE BUILDING\nThe Intensive is a 14-step guided path. By the end, you''ll have:\n- Your Life Vision written across 12 categories, built with VIVA\n- A personalized Activation Audio (morning + evening)\n- A Vision Board with images for each category\n- Your first Conscious Creation Journal entry\n- A live Calibration Call booked\n- A 28-Day Activation Plan to make it your daily rhythm\n\nThat''s what "done" looks like. Not "I read about it." You''ll have a living system drafted, recorded, visualized, and scheduled.\n\nYOUR FIRST 24 HOURS: 3 THINGS\n1. Log in and set your password\n2. Start your Intensive - click "Start My Activation Intensive" on your dashboard\n3. Complete Steps 1-4 (Account, Intake, Profile, Assessment)\n\nPlan on one focused session (about 60-90 minutes) to knock these out.\n\nLog in: https://vibrationfit.com/auth/login\n\nYOUR $1 FOUNDING TESTER ACCESS\nWhat you have right now: Full access to the 72-Hour Activation Intensive + 8 weeks of Vision Pro.\nWhat happens on Day 56: Your selected plan begins automatically.\n- Annual: $999/year\n- 28-Day: $99 every 28 days\nYou can switch plans or cancel anytime before Day 56. No charge beyond your $1.\n\nNo surprises. No fine print.\n\nThe Vibration Fit Team',
  '["firstName", "email"]'::jsonb,
  '["Sequence: Intensive Onboarding step 2 (1 hour after purchase)"]'::jsonb
),

-- -------------------------------------------------------
-- Bucket 4, Day 1: Start + Foundations
-- -------------------------------------------------------
(
  'intensive-day1-start',
  'Intensive: Day 1 - Start & Foundations',
  'Day 1 guidance. Login, hit Start, complete Steps 1-4. Skipped if assessment already completed.',
  'intensive',
  'active',
  'Today: Start your Activation Intensive',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Day 1 of 3</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Start + Foundations</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Welcome officially inside the 72-Hour Activation Intensive.</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Today''s Focus</p>\n    <p style="margin:0 0 16px;font-size:15px;color:#E5E5E5;">Login, hit Start, and complete Steps 1-4.</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">1. Log in to your account</p>\n    <p style="margin:0 0 16px;font-size:15px;color:#39FF14;font-weight:600;">2. Click "Start My Activation Intensive"</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">This is what officially begins your 72-hour focus window.</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">3. Complete Steps 1-4</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Account Settings</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Baseline Intake</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Create Your Profile</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; Vibration Assessment (84 Questions)</p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="https://vibrationfit.com/auth/login" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Go to My Intensive</a></td></tr></table>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">This first session is your foundation. Everything that follows -- your Vision, Audios, and Board -- is built on what you put in here.</p>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">You''re also helping us improve Vibration Fit with every click, so if anything feels confusing or clunky, hit reply and tell us.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">With appreciation,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'TODAY: START YOUR ACTIVATION INTENSIVE\n\nHi {{firstName}},\n\nWelcome officially inside the 72-Hour Activation Intensive.\n\nTODAY''S FOCUS\nLogin, hit Start, and complete Steps 1-4.\n\n1. Log in to your account\nhttps://vibrationfit.com/auth/login\n\n2. Click "Start My Activation Intensive"\nThis is what officially begins your 72-hour focus window.\n\n3. Complete Steps 1-4\n- Account Settings\n- Baseline Intake\n- Create Your Profile\n- Vibration Assessment (84 Questions)\n\nThis first session is your foundation. Everything that follows -- your Vision, Audios, and Board -- is built on what you put in here.\n\nYou''re also helping us improve Vibration Fit with every click, so if anything feels confusing or clunky, hit reply and tell us.\n\nWith appreciation,\nThe Vibration Fit Team',
  '["firstName"]'::jsonb,
  '["Sequence: Intensive Onboarding step 3 (24h after purchase)", "Skip if: assessment_completed = true"]'::jsonb
),

-- -------------------------------------------------------
-- Bucket 4, Day 2: Vision + Audio
-- -------------------------------------------------------
(
  'intensive-day2-vision-audio',
  'Intensive: Day 2 - Vision & Audio',
  'Day 2 guidance. Build Life Vision, Refine, Generate Audio, Record, Mix. Skipped if audio already generated.',
  'intensive',
  'active',
  E'Today: Build your Vision + Activation Audio',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Day 2 of 3</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Vision + Audio</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Nice work getting your foundations in place.</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Today''s Focus</p>\n    <p style="margin:0 0 16px;font-size:15px;color:#E5E5E5;">Turn your answers into a clear Life Vision and your first Activation Audio.</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">Complete these steps next:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Build Your Life Vision (12 Categories)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Refine Your Vision with VIVA</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Generate Your Activation Audio</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Record Your Voice</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; Create an Audio Mix</p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="https://vibrationfit.com/auth/login" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Go to My Intensive</a></td></tr></table>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">By the end of today, you''ll have a <strong style="color:#fff;">personalized audio</strong> you can listen to on repeat to stay in harmony with your vision.</p>\n\n  <div style="padding:16px;background-color:#000;border-radius:12px;border:1px solid #333;margin-bottom:24px;">\n    <p style="margin:0 0 8px;font-size:14px;color:#fff;font-weight:600;">If you hit anything confusing or broken:</p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">&#8226; Click the Support button on the menu or go to <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a></p>\n    <p style="margin:0 0 4px;font-size:13px;color:#E5E5E5;">&#8226; Choose "Bug Report" in the category dropdown if something isn''t working</p>\n    <p style="margin:0;font-size:13px;color:#E5E5E5;">&#8226; Choose the other categories for general questions or billing</p>\n  </div>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">Your feedback is extremely important to us as it is shaping how thousands of people will experience this later!</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">You''re doing the real work,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'TODAY: BUILD YOUR VISION + ACTIVATION AUDIO\n\nHi {{firstName}},\n\nNice work getting your foundations in place.\n\nTODAY''S FOCUS\nTurn your answers into a clear Life Vision and your first Activation Audio.\n\n1. Log back into your account\nhttps://vibrationfit.com/auth/login\n\n2. Complete these steps next:\n- Build Your Life Vision (12 Categories)\n- Refine Your Vision with VIVA\n- Generate Your Activation Audio\n- Record Your Voice\n- Create an Audio Mix\n\nBy the end of today, you''ll have a personalized audio you can listen to on repeat to stay in harmony with your vision.\n\nIf you hit anything confusing or broken:\n- Click the Support button or go to vibrationfit.com/support\n- Choose "Bug Report" if something isn''t working\n- Choose other categories for general questions or billing\n\nYour feedback is extremely important to us as it is shaping how thousands of people will experience this later!\n\nYou''re doing the real work,\nThe Vibration Fit Team',
  '["firstName"]'::jsonb,
  '["Sequence: Intensive Onboarding step 4 (48h after purchase)", "Skip if: audio_generated = true"]'::jsonb
),

-- -------------------------------------------------------
-- Bucket 4, Day 3: Activation + Completion
-- -------------------------------------------------------
(
  'intensive-day3-activation',
  'Intensive: Day 3 - Activation & Completion',
  'Day 3 guidance. Vision Board, Journal, Call, MAP, Unlock Platform. Skipped if already unlocked.',
  'intensive',
  'active',
  E'Today: Activate your vision & unlock your platform',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Day 3 of 3</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">Activation + Completion</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">You''re in the final stretch of your <strong style="color:#fff;">72-Hour Activation Intensive</strong>.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">By the end of today, you''ll have your vision <strong style="color:#39FF14;">activated</strong> and your full Vibration Fit platform <strong style="color:#39FF14;">unlocked.</strong></p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:11px;color:#39FF14;text-transform:uppercase;letter-spacing:1px;font-weight:600;">Today''s Focus</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">1. Activation (Steps 10-12)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Vision Board</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Journal Entry</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">&#8226; Book Calibration Call</p>\n    <p style="margin:0 0 8px;font-size:15px;color:#39FF14;font-weight:600;">2. Completion (Steps 13-14)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; MAP (My Activation Plan)</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; <strong style="color:#fff;">Unlock Platform</strong></p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="https://vibrationfit.com/auth/login" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Go to My Next Step</a></td></tr></table>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">What "Unlock Platform" actually does</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;">When you reach Step 14, you''ll see:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; A Journey Summary with green checks across all 14 steps</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; An Activation Snapshot (before/after comparison)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; One final question: "What feels most different for you?"</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">&#8226; A choice on how we can share your success</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;">When you click <strong style="color:#39FF14;">Unlock Platform</strong>:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Your Intensive is marked completed</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Your main dashboard unlocks</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; You get the full Vibration Fit experience</p>\n  </div>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;font-weight:600;">This is your graduation moment.</p>\n\n  <p style="margin:0;font-size:15px;line-height:1.6;color:#E5E5E5;">Give yourself one focused block today to complete through Unlock Platform. Don''t leave your story half-written.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">You''ve come all this way. Finish the path and step into the platform you just built for your future self.</p>\n  <p style="margin:16px 0 0;font-size:15px;color:#E5E5E5;">With you,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'TODAY: ACTIVATE YOUR VISION & UNLOCK YOUR PLATFORM\n\nHi {{firstName}},\n\nYou''re in the final stretch of your 72-Hour Activation Intensive.\n\nBy the end of today, you''ll have your vision activated and your full Vibration Fit platform unlocked.\n\nTODAY''S FOCUS\n\n1. Activation (Steps 10-12)\n- Vision Board\n- Journal Entry\n- Book Calibration Call\n\n2. Completion (Steps 13-14)\n- MAP (My Activation Plan)\n- Unlock Platform\n\nGo to my next step: https://vibrationfit.com/auth/login\n\nWHAT "UNLOCK PLATFORM" ACTUALLY DOES\nWhen you reach Step 14, you''ll see:\n- A Journey Summary with green checks across all 14 steps\n- An Activation Snapshot (before/after comparison)\n- One final question: "What feels most different for you?"\n- A choice on how we can share your success\n\nWhen you click Unlock Platform:\n- Your Intensive is marked completed\n- Your main dashboard unlocks\n- You get the full Vibration Fit experience\n\nThis is your graduation moment.\n\nGive yourself one focused block today to complete through Unlock Platform.\n\nWith you,\nThe Vibration Fit Team',
  '["firstName"]'::jsonb,
  '["Sequence: Intensive Onboarding step 5 (72h after purchase)", "Skip if: unlock_completed = true"]'::jsonb
),

-- -------------------------------------------------------
-- Bucket 5: Day 4 Debrief + Testimonial Ask (conditional)
-- -------------------------------------------------------
(
  'intensive-day4-debrief',
  'Intensive: Day 4 - Debrief & Unlock Reminder',
  'Conditional rescue email. Only sends if unlock_completed is still false. Encourages finishing Step 14.',
  'intensive',
  'active',
  E'Final step: unlock your platform & capture your results',
  E'<!DOCTYPE html>\n<html lang="en">\n<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head>\n<body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000;color:#fff;">\n<table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000;"><tr><td align="center" style="padding:40px 20px;">\n<table width="600" cellpadding="0" cellspacing="0" style="max-width:600px;">\n\n<tr><td style="padding:0 0 24px;text-align:center;"><div style="display:inline-block;padding:8px 24px;background-color:rgba(57,255,20,0.1);border-radius:50px;border:2px solid #39FF14;"><p style="margin:0;font-size:12px;font-weight:600;color:#39FF14;text-transform:uppercase;letter-spacing:1px;">Final Step</p></div></td></tr>\n\n<tr><td style="padding:0;"><div style="padding:40px;background-color:#1F1F1F;border-radius:16px;border:2px solid #39FF14;">\n\n  <h1 style="margin:0 0 24px;font-size:28px;font-weight:bold;color:#fff;line-height:1.2;">One last step</h1>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">Hi {{firstName}},</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">I can see you''ve made it all the way through your <strong style="color:#fff;">72-Hour Activation Intensive</strong>.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">There''s one last step: <strong style="color:#39FF14;">Unlock Platform</strong>.</p>\n\n  <p style="margin:0 0 24px;font-size:16px;line-height:1.6;color:#E5E5E5;">This is <strong style="color:#fff;">Step 14 of 14</strong>, and it does two things at once:</p>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:2px solid #39FF14;">\n    <p style="margin:0 0 16px;font-size:15px;color:#39FF14;font-weight:600;">1. Captures your before/after</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;">You''ll answer a short set of questions so the system can compare your post-intensive answers with your original Baseline Intake. You''ll literally see:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; How clear your vision feels now</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; How empowered you are to stay in vibrational harmony</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; How easy it is to create and update your vision, audios, and board</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">&#8226; How clear your MAP feels</p>\n    <p style="margin:0 0 16px;font-size:15px;color:#39FF14;font-weight:600;">2. Graduates you & unlocks your full platform</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Your Intensive is marked completed</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Your main dashboard unlocks</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; You''ll see a celebration state and your full experience opens up</p>\n  </div>\n\n  <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="https://vibrationfit.com/auth/login" style="display:inline-block;padding:18px 48px;background-color:#39FF14;color:#000;text-decoration:none;font-size:16px;font-weight:700;border-radius:50px;">Complete Unlock Platform Now</a></td></tr></table>\n\n  <div style="margin:0 0 24px;padding:24px;background-color:#000;border-radius:12px;border:1px solid #333;">\n    <p style="margin:0 0 8px;font-size:16px;font-weight:600;color:#fff;">What we''ll ask you</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;">On the Unlock page you''ll see:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; A Journey Summary showing green checks across all 14 steps</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; An Activation Snapshot with 11 quick questions (most already pre-scored)</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; One final question: "What feels most different for you?"</p>\n    <p style="margin:0 0 16px;font-size:14px;color:#E5E5E5;">&#8226; You can record a short video (up to 3 minutes) or type your response</p>\n    <p style="margin:0 0 8px;font-size:14px;color:#E5E5E5;">You''ll also choose how (or if) we can share your success:</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; With first name</p>\n    <p style="margin:0 0 4px;font-size:14px;color:#E5E5E5;">&#8226; Anonymously</p>\n    <p style="margin:0;font-size:14px;color:#E5E5E5;">&#8226; Or not at all</p>\n  </div>\n\n  <p style="margin:0 0 24px;font-size:15px;line-height:1.6;color:#E5E5E5;">You paid $1 and did the work. Finish this last step so you leave with a complete story and a fully unlocked platform.</p>\n\n  <p style="margin:0;font-size:14px;line-height:1.6;color:#999;font-style:italic;">If anything on the Unlock page is confusing or broken, click the Support button or go to <a href="https://vibrationfit.com/support" style="color:#39FF14;text-decoration:none;">vibrationfit.com/support</a> while logged in and choose "Bug Report" so we can fix it fast.</p>\n\n  <div style="height:2px;background-color:#39FF14;margin:32px 0;"></div>\n  <p style="margin:0;font-size:15px;color:#E5E5E5;">With deep appreciation for helping us build this,<br>The Vibration Fit Team</p>\n\n</div></td></tr>\n\n<tr><td style="padding:32px 20px 0;text-align:center;"><p style="margin:0 0 8px;font-size:11px;color:#666;">Vibration Fit &middot; <span style="color:#39FF14;">Above the Green Line</span></p><p style="margin:0;font-size:10px;color:#555;">You''re receiving this because you purchased the 72-Hour Activation Intensive.</p></td></tr>\n\n</table></td></tr></table>\n</body></html>',
  E'FINAL STEP: UNLOCK YOUR PLATFORM & CAPTURE YOUR RESULTS\n\nHi {{firstName}},\n\nI can see you''ve made it all the way through your 72-Hour Activation Intensive.\n\nThere''s one last step: Unlock Platform.\n\nThis is Step 14 of 14, and it does two things at once:\n\n1. CAPTURES YOUR BEFORE/AFTER\nYou''ll answer a short set of questions so the system can compare your post-intensive answers with your original Baseline Intake.\n\n2. GRADUATES YOU & UNLOCKS YOUR FULL PLATFORM\n- Your Intensive is marked completed\n- Your main dashboard unlocks\n- You''ll see a celebration state and your full experience opens up\n\nComplete Unlock Platform now: https://vibrationfit.com/auth/login\n\nWHAT WE''LL ASK YOU\n- A Journey Summary showing green checks across all 14 steps\n- An Activation Snapshot with 11 quick questions\n- One final question: "What feels most different for you?"\n- You can record a short video (up to 3 min) or type your response\n- You''ll choose how we can share your success (named, anonymous, or not at all)\n\nYou paid $1 and did the work. Finish this last step so you leave with a complete story and a fully unlocked platform.\n\nWith deep appreciation for helping us build this,\nThe Vibration Fit Team',
  '["firstName"]'::jsonb,
  '["Sequence: Intensive Onboarding step 6 (84h after purchase)", "Skip if: unlock_completed = true", "Conditional: only if not yet unlocked"]'::jsonb
)

ON CONFLICT (slug) DO UPDATE SET
  name = EXCLUDED.name, description = EXCLUDED.description, category = EXCLUDED.category,
  status = EXCLUDED.status, subject = EXCLUDED.subject, html_body = EXCLUDED.html_body,
  text_body = EXCLUDED.text_body, variables = EXCLUDED.variables,
  triggers = EXCLUDED.triggers, updated_at = NOW();


-- ============================================================================
-- 3. INTENSIVE ONBOARDING SEQUENCE
-- ============================================================================

-- Create the sequence (idempotent via DO block)
DO $$
DECLARE
  v_seq_id uuid;
  v_tpl_welcome uuid;
  v_tpl_orientation uuid;
  v_tpl_day1 uuid;
  v_tpl_day2 uuid;
  v_tpl_day3 uuid;
  v_tpl_day4 uuid;
BEGIN
  -- Upsert the sequence
  INSERT INTO public.sequences (name, description, trigger_event, trigger_conditions, exit_events, status)
  VALUES (
    'Intensive Onboarding',
    '6-step drip sequence after Intensive purchase. Welcome, orientation, then daily guidance emails (Day 1-3) with conditional skips based on intensive_checklist progress. Day 4 debrief only if not yet unlocked.',
    'intensive.purchased',
    '{}'::jsonb,
    '["intensive.completed"]'::jsonb,
    'active'
  )
  ON CONFLICT DO NOTHING;

  SELECT id INTO v_seq_id FROM public.sequences WHERE name = 'Intensive Onboarding' LIMIT 1;

  IF v_seq_id IS NULL THEN
    RAISE NOTICE 'Intensive Onboarding sequence not found, skipping step creation.';
    RETURN;
  END IF;

  -- Look up template IDs
  SELECT id INTO v_tpl_welcome FROM public.email_templates WHERE slug = 'intensive-welcome-access';
  SELECT id INTO v_tpl_orientation FROM public.email_templates WHERE slug = 'intensive-orientation';
  SELECT id INTO v_tpl_day1 FROM public.email_templates WHERE slug = 'intensive-day1-start';
  SELECT id INTO v_tpl_day2 FROM public.email_templates WHERE slug = 'intensive-day2-vision-audio';
  SELECT id INTO v_tpl_day3 FROM public.email_templates WHERE slug = 'intensive-day3-activation';
  SELECT id INTO v_tpl_day4 FROM public.email_templates WHERE slug = 'intensive-day4-debrief';

  IF v_tpl_welcome IS NULL OR v_tpl_orientation IS NULL OR v_tpl_day1 IS NULL
     OR v_tpl_day2 IS NULL OR v_tpl_day3 IS NULL OR v_tpl_day4 IS NULL THEN
    RAISE NOTICE 'One or more intensive email templates not found. Skipping step creation.';
    RETURN;
  END IF;

  -- Delete existing steps for this sequence (idempotent re-run)
  DELETE FROM public.sequence_steps WHERE sequence_id = v_seq_id;

  -- Step 1: Welcome (immediate, always send)
  INSERT INTO public.sequence_steps (sequence_id, step_order, channel, template_id, delay_minutes, delay_from, conditions, status)
  VALUES (v_seq_id, 1, 'email', v_tpl_welcome, 0, 'enrollment', '{}'::jsonb, 'active');

  -- Step 2: Orientation (60 min after enrollment, always send)
  INSERT INTO public.sequence_steps (sequence_id, step_order, channel, template_id, delay_minutes, delay_from, conditions, status)
  VALUES (v_seq_id, 2, 'email', v_tpl_orientation, 60, 'enrollment', '{}'::jsonb, 'active');

  -- Step 3: Day 1 (24h = 1440 min, skip if assessment already completed)
  INSERT INTO public.sequence_steps (sequence_id, step_order, channel, template_id, delay_minutes, delay_from, conditions, status)
  VALUES (v_seq_id, 3, 'email', v_tpl_day1, 1440, 'enrollment',
    '{"skip_if_checklist": {"table": "intensive_checklist", "user_field": "user_id", "check_field": "assessment_completed", "check_value": true}}'::jsonb,
    'active');

  -- Step 4: Day 2 (48h = 2880 min, skip if audio already generated)
  INSERT INTO public.sequence_steps (sequence_id, step_order, channel, template_id, delay_minutes, delay_from, conditions, status)
  VALUES (v_seq_id, 4, 'email', v_tpl_day2, 2880, 'enrollment',
    '{"skip_if_checklist": {"table": "intensive_checklist", "user_field": "user_id", "check_field": "audio_generated", "check_value": true}}'::jsonb,
    'active');

  -- Step 5: Day 3 (72h = 4320 min, skip if already unlocked)
  INSERT INTO public.sequence_steps (sequence_id, step_order, channel, template_id, delay_minutes, delay_from, conditions, status)
  VALUES (v_seq_id, 5, 'email', v_tpl_day3, 4320, 'enrollment',
    '{"skip_if_checklist": {"table": "intensive_checklist", "user_field": "user_id", "check_field": "unlock_completed", "check_value": true}}'::jsonb,
    'active');

  -- Step 6: Day 4 Debrief (84h = 5040 min, skip if already unlocked)
  INSERT INTO public.sequence_steps (sequence_id, step_order, channel, template_id, delay_minutes, delay_from, conditions, status)
  VALUES (v_seq_id, 6, 'email', v_tpl_day4, 5040, 'enrollment',
    '{"skip_if_checklist": {"table": "intensive_checklist", "user_field": "user_id", "check_field": "unlock_completed", "check_value": true}}'::jsonb,
    'active');

  RAISE NOTICE 'Intensive Onboarding sequence created with 6 steps (seq_id: %)', v_seq_id;
END $$;
